INSERT INTO `hesk_replies` VALUES (62, '20', 'Ian Perks', 'Hi Peter,<br />\r\n <br />\r\nI am following up on your request via the Helpdesk in December regarding the download icons which display on the VisitKielder website.<br />\r\n <br />\r\nI have been working with Mark Bishop at One North East on creating the coding which would allow us to alter these icons and this work is now complete.<br />\r\n <br />\r\nNow when you upload files to DestiNE records using the ''Media'' tab, the relevant icon will display in accordance with the ''Media Type'' selected. The following media types are active, select the relevant media type for the associated icon to be displayed on the website:<br />\r\n <br />\r\nMEDIA TYPE -&gt; ICON DISPLAYED<br />\r\nBrochure -&gt; Pile of Books<br />\r\nGraphics -&gt; Camera<br />\r\nMap -&gt; Pin<br />\r\nSound -&gt; Music Note<br /><br />Ian Perks<br />\r\nNorthumberland Tourism Online Marketing<br />\r\n<br />\r\n<a href="mailto:ian.perks@northumberlandtourism.co.uk">ian.perks@northumberlandtourism.co.uk</a><br />\r\n<a href="http://www.VisitNorthumberland.com" target="_blank">www.VisitNorthumberland.com</a><br />&nbsp;', '2010-01-20 15:50:51', '', 1, NULL);
