INSERT INTO `hesk_replies` VALUES (72, '64', 'Ian Perks', 'Hi Philippa<br />\r\n<br />\r\nThis is a common problem with users who do not have Microsoft Word installed or configured correctly on their computer. If they don''t have it installed, their computer will try and use the next best thing - in this case Notepad.<br />\r\n<br />\r\nThe way around this is to replace Word documents with PDF versions. Attached is a link to the microsoft website where you can download a tool which works inside Microsoft Word and converts word documents into PDFs.<br />\r\n<br />\r\nSo when you want to turn a word document into a PDF you simply go to Save As and then select PDF. This can then be uploaded onto the website.<br />\r\n<br />\r\n<a href="http://office.microsoft.com/en-us/help/HA101675271033.aspx" target="_blank">http://office.microsoft.com/en-us/help/HA101675271033.aspx</a><br />\r\n<br />\r\nAny queries, just let me know.<br />\r\n<br />\r\nAll the best<br />\r\n<br />\r\nIan<br /><br />Ian Perks<br />\r\nNorthumberland Tourism Online Marketing<br />\r\n<br />\r\n<a href="mailto:ian.perks@northumberlandtourism.co.uk">ian.perks@northumberlandtourism.co.uk</a><br />\r\n<a href="http://www.VisitNorthumberland.com" target="_blank">www.VisitNorthumberland.com</a><br />&nbsp;', '2010-02-01 17:32:21', '', 1, NULL);
