INSERT INTO `hesk_replies` VALUES (110, '112', 'Ian Perks', 'Dear Mr Hewison,<br />\r\n<br />\r\nThank you for letting us know about the search refine facility. The reason they''re all ticked on the search results screens is so that all properties are displayed to begin with, then the user can remove the gradings they do not wish to see. <br />\r\n<br />\r\nWe''re looking into adding a gradings selection option into the initial search box so the user has the choice to search for properties with specific gradings.<br />\r\n<br />\r\nKind Regards<br />\r\n<br />\r\nIan<br /><br />Ian Perks<br />\r\nNorthumberland Tourism Online Marketing<br />\r\n<br />\r\n<a href="mailto:ian.perks@northumberlandtourism.co.uk">ian.perks@northumberlandtourism.co.uk</a><br />\r\n<a href="http://www.VisitNorthumberland.com" target="_blank">www.VisitNorthumberland.com</a><br />&nbsp;', '2010-04-16 11:42:38', '', 1, '1');
