INSERT INTO `hesk_replies` VALUES (137, '134', 'Ian Perks', 'Thank you for contacting us regarding your availability not showing on the visitnorthumberland website.<br />\r\n<br />\r\nI advise calling Eviivo customer support on 0208 576 5603 to ensure that your ''opt-in'' channels include the visit Northumberland website - this is called ''ONE_VISIT_NORTHUMBERLAND''. This will allow the information to synchronise across to the visitnorthumberland website.<br />\r\n<br />\r\nKind Regards<br />\r\n<br />\r\nIan<br /><br />Ian Perks<br />\r\nNorthumberland Tourism Online Marketing<br />\r\n<br />\r\n<a href="mailto:ian.perks@northumberlandtourism.co.uk">ian.perks@northumberlandtourism.co.uk</a><br />\r\n<a href="http://www.VisitNorthumberland.com" target="_blank">www.VisitNorthumberland.com</a><br />&nbsp;', '2010-06-29 16:50:04', '', 1, NULL);
