INSERT INTO `hesk_replies` VALUES (155, '145', 'Ian Perks', 'Hi Chris<br />\r\n<br />\r\nThank you for contacting us. That woudl be great to get your store on our online merchant shopping page. We''ll get you properly on our tourism database so you''ll appear on other official visitor websites as well.<br />\r\n<br />\r\nThe page you''d appear on with the Visit Northumberland website would be <a href="http://www.visitnorthumberland.com/site/shopping/online-merchants" target="_blank">http://www.visitnorthumberland.com/site/shopping/online-merchants</a><br />\r\n<br />\r\nI have attached a data collection form to this message. Please fill in as much as is appropriate (use the short description box) and then email it back to our data team - <a href="mailto:update@northumberlandtourism.co.uk">update@northumberlandtourism.co.uk</a><br />\r\n<br />\r\nIf you have any queries, please feel free to call.<br />\r\n<br />\r\nKind Regards<br />\r\n<br />\r\nIan<br /><br />Ian Perks<br />\r\nNorthumberland Tourism Online Marketing<br />\r\n<br />\r\n<a href="mailto:ian.perks@northumberlandtourism.co.uk">ian.perks@northumberlandtourism.co.uk</a><br />\r\n<a href="http://www.VisitNorthumberland.com" target="_blank">www.VisitNorthumberland.com</a><br />&nbsp;', '2010-08-04 15:26:24', '22#northern_heritage.doc,', 1, NULL);
