INSERT INTO `hesk_replies` VALUES (171, '168', 'Ian Perks', 'Thank you for contacting us regarding Cara Guesthouse. Whitley bay is not within the county of Northumberland, so I''m afraid we cannot assist you directly.<br />\r\n<br />\r\nThe Whitley Bay area is covered by NGI (Newcastle Gateshead Initiative), their website is <a href="http://www.newcastlegateshead.com." target="_blank">www.newcastlegateshead.com.</a> <br />\r\n<br />\r\nAlso, the company which issues the tourist board gradings is called ''Quality in Tourism''. Their website is <a href="http://www.qualityintourism.com" target="_blank">www.qualityintourism.com</a> - this would be the company to notify of the problems you are experiencing.<br />\r\n<br />\r\nKind Regards<br />\r\n<br />\r\nIan<br /><br />Ian Perks<br />\r\nNorthumberland Tourism Online Marketing<br />\r\n<br />\r\n<a href="mailto:ian.perks@northumberlandtourism.co.uk">ian.perks@northumberlandtourism.co.uk</a><br />\r\n<a href="http://www.VisitNorthumberland.com" target="_blank">www.VisitNorthumberland.com</a><br />&nbsp;', '2010-09-24 10:19:03', '', 1, NULL);
