INSERT INTO `hesk_replies` VALUES (177, '172', 'Ian Perks', 'Dear Mrs Warwick<br />\r\n<br />\r\nThank you for contacting us about this error and I am sorry that it occurred. We are currently liaising with the accommodation provider and our booking systems provider to find out why the wrong cottage was booked. The accommodation provider has received your message for cancellation and has also reported this issue to us.<br />\r\n<br />\r\nIf you’d like assistance with finding a cottage, the Alnwick Tourist Information Centre would be happy to assist – 01665 511 333.<br />\r\n<br />\r\nKind regards and apologies again<br />\r\n<br />\r\nIan<br /><br />Ian Perks<br />\r\nNorthumberland Tourism Online Marketing<br />\r\n<br />\r\n<a href="mailto:ian.perks@northumberlandtourism.co.uk">ian.perks@northumberlandtourism.co.uk</a><br />\r\n<a href="http://www.VisitNorthumberland.com" target="_blank">www.VisitNorthumberland.com</a><br />&nbsp;', '2010-10-11 09:26:43', '', 1, NULL);
INSERT INTO `hesk_replies` VALUES (178, '172', 'Ian Perks', 'Dear Mrs Warwick,<br />\r\n<br />\r\nJust to inform you that we have finished talks with the accommodation provider and our online booking service providers. <br />\r\n<br />\r\nA refund will be made to your payment card within 7 days.<br />\r\n<br />\r\nI can only apologise again for this error.<br />\r\n<br />\r\nKind Regards<br />\r\n<br />\r\nIan<br /><br />Ian Perks<br />\r\nNorthumberland Tourism Online Marketing<br />\r\n<br />\r\n<a href="mailto:ian.perks@northumberlandtourism.co.uk">ian.perks@northumberlandtourism.co.uk</a><br />\r\n<a href="http://www.VisitNorthumberland.com" target="_blank">www.VisitNorthumberland.com</a><br />&nbsp;', '2010-10-11 10:37:22', '', 1, NULL);
