INSERT INTO `hesk_replies` VALUES (181, '175', 'Ian Perks', 'Hi Beryl, <br />\r\n<br />\r\nThank you for contacting us regarding getting your property listed on the VisitNorthumberland.com website.<br />\r\n<br />\r\nTo be able to be listed your accommodation needs to have an official quality grading. If this is the case, please send an email to <a href="mailto:update@northumberlandtourism.co.uk">update@northumberlandtourism.co.uk</a> with the name of your property and one of the team will contact you shortly.<br />\r\n<br />\r\nKind Regards<br />\r\n<br />\r\nIan<br /><br />Ian Perks<br />\r\nNorthumberland Tourism Online Marketing<br />\r\n<br />\r\n<a href="mailto:ian.perks@northumberlandtourism.co.uk">ian.perks@northumberlandtourism.co.uk</a><br />\r\n<a href="http://www.VisitNorthumberland.com" target="_blank">www.VisitNorthumberland.com</a><br />&nbsp;', '2010-10-20 09:18:17', '', 1, '5');
