INSERT INTO `hesk_replies` VALUES (187, '178', 'Ian Perks', 'Hi Geoff,<br />\r\n<br />\r\nThank you for contacting us regarding the award markers for Harbourside House, Craster.<br />\r\n<br />\r\nThese awards are provided by ''Quality in Tourism'', the company which inspects properties and gives the official tourist grading. <br />\r\n<br />\r\nSo at present you have the ''Welcome Pets'' award which started on 07/07/10. To be able to get the other awards you need to apply to Quality in Tourism.<br />\r\n<br />\r\nHere is a useful web page which contains further information on how to apply for the awards at the bottom of the page: <a href="http://www.qualityintourism.com/asp/selfcatering.asp" target="_blank">http://www.qualityintourism.com/asp/selfcatering.asp</a><br />\r\n<br />\r\nKind Regards<br />\r\n<br />\r\nIan<br /><br />Ian Perks<br />\r\nNorthumberland Tourism Online Marketing<br />\r\n<br />\r\n<a href="mailto:ian.perks@northumberlandtourism.co.uk">ian.perks@northumberlandtourism.co.uk</a><br />\r\n<a href="http://www.VisitNorthumberland.com" target="_blank">www.VisitNorthumberland.com</a><br />&nbsp;', '2010-10-30 13:22:31', '', 1, NULL);
