INSERT INTO `hesk_replies` VALUES (202, '197', 'Ian Perks', 'Mr Ward,<br />\r\n<br />\r\nThank you for your feedback regarding transport around Northumberland. This is a section of the website we will be working on shortly.<br />\r\n<br />\r\nFor the time being, please call one of our Tourist Information Centres - <a href="http://www.visitnorthumberland.com/site/tourist-information-centres" target="_blank">http://www.visitnorthumberland.com/site/tourist-information-centres</a> - in the area of Northumberland you are interested in visiting. They will be able to send out timetables and advise you on the best methods of transport around the county.<br />\r\n<br />\r\nKind Regards<br />\r\n<br />\r\nIan<br /><br />Ian Perks<br />\r\nNorthumberland Tourism Online Marketing<br />\r\n<br />\r\n<a href="mailto:ian.perks@northumberlandtourism.co.uk">ian.perks@northumberlandtourism.co.uk</a><br />\r\n<a href="http://www.VisitNorthumberland.com" target="_blank">www.VisitNorthumberland.com</a><br />&nbsp;', '2011-01-31 10:18:10', '', 1, NULL);
