INSERT INTO `hesk_replies` VALUES (205, '201', 'Ian Perks', 'Hi Deb,<br />\r\n<br />\r\nThank you for contacting us regarding the Allendale page. We have recently been updating all of the town pages and putting them live. The updated Allendale page (along with several others) - <a href="http://www.visitnorthumberland.com/site/allendale" target="_blank">http://www.visitnorthumberland.com/site/allendale</a> - has just gone live this morning (Feb 2nd) with the accommodation links. <br />\r\n<br />\r\nWe''re also going to be adding image slideshows and useful toolks to the town pages shortly.<br />\r\n<br />\r\nKind Regards<br />\r\n<br />\r\nIan<br /><br />Ian Perks<br />\r\nNorthumberland Tourism Online Marketing<br />\r\n<br />\r\n<a href="mailto:ian.perks@northumberlandtourism.co.uk">ian.perks@northumberlandtourism.co.uk</a><br />\r\n<a href="http://www.VisitNorthumberland.com" target="_blank">www.VisitNorthumberland.com</a><br />&nbsp;', '2011-02-02 11:57:45', '', 1, NULL);
