INSERT INTO `hesk_replies` VALUES (211, '204', 'Ian Perks', 'Mr Taylor,<br />\r\n<br />\r\nThank you for contacting us regarding accommodation along The Reivers Way. <br />\r\n<br />\r\nI have sent you a free Northumberland holiday guide. I also suggest you call the Alnwick Tourist Information Centre 01665 511333 or the Rothburty Tourist Information Centre 01669 620887 to see if they have any further information they can send you.<br />\r\n<br />\r\nWe are currently working on our website to get such accommodation lists for long distance walking routes up and running soon.<br />\r\n<br />\r\nKind Regards<br />\r\n<br />\r\nIan<br /><br />Ian Perks<br />\r\nNorthumberland Tourism Online Marketing<br />\r\n<br />\r\n<a href="mailto:ian.perks@northumberlandtourism.co.uk">ian.perks@northumberlandtourism.co.uk</a><br />\r\n<a href="http://www.VisitNorthumberland.com" target="_blank">www.VisitNorthumberland.com</a><br />&nbsp;', '2011-02-08 13:03:17', '', 1, NULL);
