INSERT INTO `hesk_replies` VALUES (263, '252', 'Ian Perks', 'Mr Robson,<br />\r\n<br />\r\nThank you for contacting us regarding the Pitmen Painters collection.<br />\r\n<br />\r\nThere is no mainstream way of purchasing these paintings and the majority of them either belong to the Pitmen Painters Trust group at Woodhorn, Northumberland or to the families of the artists.<br />\r\n<br />\r\nThere are occasional times when the paintings are available at auction, but these aren''t frequent. Auctions appear to be the only way of being able to purchase such paintings.<br />\r\n<br />\r\nKind regards<br />\r\n<br />\r\nIan<br /><br />Ian Perks<br />\r\nNorthumberland Tourism Online Marketing<br />\r\n<br />\r\n<a href="mailto:ian.perks@northumberlandtourism.co.uk">ian.perks@northumberlandtourism.co.uk</a><br />\r\n<a href="http://www.VisitNorthumberland.com" target="_blank">www.VisitNorthumberland.com</a><br />&nbsp;', '2011-05-10 15:03:28', '', 1, NULL);
