INSERT INTO `hesk_replies` VALUES (319, '316', 'Ian Perks', 'Hi Dan,<br />\r\n<br />\r\nThank you for contacting us. I suggest sending an email to <a href="mailto:advertising@northumberlandtourism.co.uk">advertising@northumberlandtourism.co.uk</a> to discuss your requirements. My colleagues will be happy to help you.<br />\r\n<br />\r\nKind regards<br />\r\n<br />\r\nIan<br /><br />Ian Perks<br />\r\nNorthumberland Tourism Online Marketing<br />\r\n<br />\r\n<a href="mailto:ian.perks@northumberlandtourism.co.uk">ian.perks@northumberlandtourism.co.uk</a><br />\r\n<a href="http://www.VisitNorthumberland.com" target="_blank">www.VisitNorthumberland.com</a><br />&nbsp;', '2011-11-17 11:56:11', '', 1, NULL);
INSERT INTO `hesk_replies` VALUES (320, '315', 'Ian Perks', 'Hi Anne,<br />\r\n<br />\r\nThank you for contacting us.<br />\r\n<br />\r\nYou can add events quickly on the following page: <a href="http://www.visitnorthumberland.com/events/add-an-event" target="_blank">http://www.visitnorthumberland.com/events/add-an-event</a><br />\r\n<br />\r\nKind regards<br />\r\nIan<br /><br />Ian Perks<br />\r\nNorthumberland Tourism Online Marketing<br />\r\n<br />\r\n<a href="mailto:ian.perks@northumberlandtourism.co.uk">ian.perks@northumberlandtourism.co.uk</a><br />\r\n<a href="http://www.VisitNorthumberland.com" target="_blank">www.VisitNorthumberland.com</a><br />&nbsp;', '2011-11-17 12:30:03', '', 1, NULL);
INSERT INTO `hesk_replies` VALUES (321, '318', 'Ian Perks', 'Dear Ellen,<br />\r\n<br />\r\nThank you for contacting us and for your feedback. I''m very glad you had an enjoyable holiday and our colleagues in Berwick TIC were able to help you.<br />\r\n<br />\r\nI have also forwarded on your message onto the Berwick staff.<br />\r\n<br />\r\nHave a good weekend.<br />\r\n<br />\r\nKind regards,<br />\r\n<br />\r\nIan<br /><br />Ian Perks<br />\r\nNorthumberland Tourism Online Marketing<br />\r\n<br />\r\n<a href="mailto:ian.perks@northumberlandtourism.co.uk">ian.perks@northumberlandtourism.co.uk</a><br />\r\n<a href="http://www.VisitNorthumberland.com" target="_blank">www.VisitNorthumberland.com</a><br />&nbsp;', '2011-11-18 16:40:17', '', 1, NULL);
INSERT INTO `hesk_replies` VALUES (322, '326', 'Ian Perks', 'Hi Margaret,<br />\r\n<br />\r\nThank you for contacting us regarding Berwick Jigsaw Puzzles.<br />\r\n<br />\r\nI suggest giving our colleagues at Berwick Tourist Information Centre a call on 01289 301780 to see if they have any in stock.<br />\r\n<br />\r\nWe''re hoping to add Berwick jigsaws to our online store shortly, but if you''re in a hurry for Christmas, contacting Berwick Tourist Information Centre will be the best option.<br />\r\n<br />\r\nKind regards and have a great Christmas.<br />\r\n<br />\r\nIan<br /><br />Ian Perks<br />\r\nNorthumberland Tourism Online Marketing<br />\r\n<br />\r\n<a href="mailto:ian.perks@northumberlandtourism.co.uk">ian.perks@northumberlandtourism.co.uk</a><br />\r\n<a href="http://www.VisitNorthumberland.com" target="_blank">www.VisitNorthumberland.com</a><br />&nbsp;', '2011-12-15 09:37:39', '', 1, NULL);
INSERT INTO `hesk_replies` VALUES (323, '327', 'Ian Perks', 'Hi Carole,<br />\r\n<br />\r\nThank you for contacting us regarding a problem you are having with the Longhirst Hall competition at <a href="http://www.visitnorthumberland.com/accommodation/competitions/longhirst-hall-competition." target="_blank">http://www.visitnorthumberland.com/accommodation/competitions/longhirst-hall-competition.</a><br />\r\n<br />\r\nIf you''d like to reply to this message with your details I shall enter your details into the competition for you.<br />\r\n<br />\r\nI apologise for the inconvenience.<br />\r\n<br />\r\nKind regards,<br />\r\n<br />\r\nIan<br /><br />Ian Perks<br />\r\nNorthumberland Tourism Online Marketing<br />\r\n<br />\r\n<a href="mailto:ian.perks@northumberlandtourism.co.uk">ian.perks@northumberlandtourism.co.uk</a><br />\r\n<a href="http://www.VisitNorthumberland.com" target="_blank">www.VisitNorthumberland.com</a><br />&nbsp;', '2011-12-15 11:43:02', '', 1, NULL);
