INSERT INTO `hesk_tickets` VALUES (64, '27QMT9ZD2T', 'Philippa Clark', 'philippa.clark@nwl.co.uk', 8, '2', 'complaint', 'hello ian <br />\r\n<br />\r\ni''ve had a customer and a staff member complain that when they try to open the watersports document from their home pc''s it doesn''t open or if it does, it only opens in a note pad version and the text is in symbols. It opens fine in a Word doc on my PC so I don''t know what to suggest to them? <br />\r\n<br />\r\nIt''s on the water activities page and its the bit that says ''To begin arranging your trip, click here to download our guide which details the best club to contact for your chosen sport.'' <br />\r\n<br />\r\ni have been told to make sure this consistently opens on all pcs - how do i do this please? <br />\r\n<br />\r\nthanks, philippa', '2010-02-01 16:18:33', '2010-02-01 17:56:36', '194.72.237.12', '3', '0', '0', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
