INSERT INTO `hesk_tickets` VALUES (66, '5TUWA1R2JG', 'Mike Hosken', 'mhosken@webandmail.co.uk', 3, '3', 'Felbridge Tower', 'When browsing an images site I came across a picture of &quot;Felbridge Tower, Northumberland&quot;.  But Google, Streetmap, Ordnance Survey can''t find it for me.  Nor can &quot;Search&quot; on your site.<br />\r\nAny idea where it is?  If it IS in Northumberland can it be included in your site?', '2010-02-05 10:24:17', '2010-02-05 11:49:57', '86.27.44.68', '3', '1', '0', '8#FelbridgeTwr.jpg,', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `hesk_tickets` VALUES (67, 'BXR6DA2ZJ5', 'Mike', 'mike@seahouseswebsite.co.uk', 3, '3', 'seahouseswebsite.co.uk', 'You already have a link to the Seahouses official site on your <a href="http://www.visitnorthumberland.com/site/destination-guide/towns-and-villages/seahouses." target="_blank">http://www.visitnorthumberland.com/site/destination-guide/towns-and-villages/seahouses.</a><br />\r\nWill it be possible for you to add a link to the &quot;friendly and useful&quot; one at -<br />\r\n<a href="http://www.seahouseswebsite.co.uk" target="_blank">http://www.seahouseswebsite.co.uk</a><br />\r\n- please?', '2010-02-06 17:03:59', '2010-02-07 12:53:35', '86.31.0.140', '3', '0', '0', '9#WebsiteHeader.gif,', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
