INSERT INTO `hesk_tickets` VALUES (69, 'U7Y1BWAP1M', 'Elisabeth Rowark', 'Elisabeth.rowark@kielderpartnership.co.uk', 6, '1', 'Accommodation', 'Hi - Brownrigg caravan site in Bellingham is not showing up on the searches through Visit Kielder site.  I suspect the marketing channels are not all ticked.  Carole and Barry have raised this with me - can you put right please.<br />\r\n<br />\r\nThanks', '2010-02-11 13:52:32', '2010-02-15 10:21:48', '194.72.237.12', '3', '1', '0', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `hesk_tickets` VALUES (70, 'DZ5UGZZ192', 'Philippa Clark', 'philippa.clark@nwl.co.uk', 1, '3', 'Outdoors show web banners', 'Hi Ian <br />\r\n<br />\r\nJude sent me some web banners and asked me to put them on visitkielder to help promote the outdoors show. the one we like best is 400 x 90. i just can''t make it fit anywhere! <br />\r\n<br />\r\ni was thinking it should sit on the walking, cycling and mountain biking pages under the buttons on the right hand side. can you tell me which size will best fit there please? <br />\r\n<br />\r\ni know we can''t have necessarily have the one we like the design of the best and we''ll just have to go with which one fits best. <br />\r\n<br />\r\nthanks, philippa', '2010-02-17 13:49:56', '2010-02-17 13:49:56', '194.72.237.12', '3', '0', '0', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
