INSERT INTO `hesk_tickets` VALUES (138, 'LENHD3GSP2', 'mrs mary evans', 'granny2006@hotmail.co.uk', 5, '2', 'poor standards', 'live in the NE, love northumberland - visit and stay there regularly.<br />\r\nproud of the NE and what can be offered.<br />\r\nrecently visited Craster and went to the pub there for a pot of tea - thankfully, there was no food on offer. The place looked as badly in need of a good scrub from top to bottom, an open door(private) displayed a chaotic pile of junk, my tea was served on a disgusting dirty tray, complete with remains of old food.<br />\r\nHow can people be allowed to get away with spoiling this area with such poor standards?<br />\r\nMrs M Evans<br />\r\n3 Devon Walk<br />\r\nWashington<br />\r\nNE37 2 ns<br />\r\n<br />\r\n<br />\r\n<br />\r\n<br />\r\n<br />\r\nHow can', '2010-07-06 20:34:40', '2010-07-06 20:34:40', '213.107.52.75', '3', '0', '0', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
