INSERT INTO `hesk_tickets` VALUES (144, 'NP1R8ENU5N', 'George Macintyre', 'george.macintyre@sky.com', 3, '2', 'website link', 'My name is George Macintyre. I took early retirement/redundancy from The Journal in September but still do the two weekly game angling columns for the newspaper. I also started the website <a href="http://www.fishingforanglers.com" target="_blank">www.fishingforanglers.com</a> to cover freshwater fishing in the North East of England. This involves weekly coverage from Chatton, Caistron Lakes, South Linden, Kielder Water fisheries as well as regular reports from the Hirst Park Trout Ticklers. Thius is a hobby site/not for profit. Would it be possible to have a link to it from the Visit Northumberland website. Thank you.', '2010-07-17 11:24:12', '2010-08-04 15:37:40', '90.201.232.33', '3', '1', '0', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
