INSERT INTO `hesk_tickets` VALUES (302, 'QD3G7H3EWQ', 'MARJORIE BAILLIE', 'marjbaillie@gmail.com', 3, '1', 'Haltwhistle Autumn Walking Festival', 'I can''t see the 18th Haltwhistle Walking Festival in the Walking Events section. The dates are 8th - 16th October. Full details on <a href="http://www.haltwhistlewalkingfestival.org" target="_blank">www.haltwhistlewalkingfestival.org</a><br />\r\nWalks vary from 4 to 14 miles and from easy to strenuous. There are 22 walks and events on offer in a walking festival run entirely by volunteers.<br />\r\n<br />\r\nI would really appreciate it if the walking festival could be added in to our site. Thanks very much', '2011-09-24 08:31:00', '2011-09-26 10:37:32', '217.42.27.245', '3', '1', '0', '39#poster_Marie_web.jpg,', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
