INSERT INTO `hesk_tickets` VALUES (307, '9SNMUVNW51', 'Cadiou Magalie', 'contact.transmedia@gmail.com', 5, '2', 'Trabslation into French', 'Hello,<br />\r\n<br />\r\nMy name is Magalie, I am a French freelance translator. I am specialized <br />\r\nin Website localization and tourism. I discovered your Website a few <br />\r\ndays ago, and I think a good French translation you could bring you new potential French visitors. French people love discovering natural places,<br />\r\nand I am sure many of them could be interesting in visiting the place if they could understand the informations.<br />\r\n<br />\r\nI you are interested in my services, do not hesitate to contact me, the <br />\r\nquotes are free and I would agree to translate a small part of your <br />\r\nWebsite for free, if you want to see my work.<br />\r\n<br />\r\nBest regards<br />\r\n<br />\r\n-- <br />\r\nMagalie Cadiou<br />\r\n<a href="http://www.transmedia-traduction.com" target="_blank">www.transmedia-traduction.com</a>', '2011-10-24 15:07:50', '2011-10-24 15:07:50', '2.10.250.169', '3', '0', '0', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
